	originX: 'center',
	originY: 'center',
	hasBorders: false,
	hasControls: true,
	selectable: true
  });

  inst.canvas.add(line).setActiveObject(line);
};

Arrow.prototype.isEnable = function() {
  return this.isDrawing;
}

Arrow.prototype.enable = function() {
  this.isDrawing = true;
}

Arrow.prototype.disable = function() {
  this.isDrawing = false;
